import './App.css';
import Person from './Person/Person';
import React, { useState } from 'react';
function App() {
  // const persons = [
  //   { name: 'Max', age: 22 },
  //   { name: 'Mia', age: 23 },
  //   { name: 'Rena', age: 26 },
  // ];

  const [personsState, setPersonsState] = useState({
    persons: [
      { name: 'Max', age: 22 },
      { name: 'Mia', age: 23 },
      { name: 'Rena', age: 26 },
    ],
  });
  const switchNameHandler = () => {
    // console.log('clicked');
    //  DO NOT DO THIS this.persons[0].name = 'Maximiliam';
    setState({
      persons: [
        { name: 'Maximiliam', age: 22 },
        { name: 'Mia', age: 23 },
        { name: 'Rena', age: 33 },
      ],
    });
  };
  return (
    <div className='App'>
      <h1>hi i am react</h1>
      <button onClick={switchNameHandler}>Switch Name</button>
      <Person name={persons[0].name} age={persons[0].age} />
      <Person name={persons[1].name} age={persons[1].age} />
      <Person name={persons[2].name} age={persons[2].age}>
        i am a programmer
      </Person>
    </div>
  );
}

export default App;
